  shapereactivities = zeros(Float64, dataset.numcols)
  allreactivities = zeros(Float64, dataset.numcols)
  vec1 = table[col]
  mapping, revmapping = createmapping(alignmentfile, sequence2)

  for i=1:dataset.numcols
    j = get(mapping, i, 0)
    k = 0
    if j > 0 && pairedsites2[j] > j
